mapper.select:
SELECT 

FROM afcustomers a

WHERE a.id = #{id}

SELECT 

FROM afcustomers a




		AND a.code = #{code}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.age = #{age}


		AND a.gender = #{gender}


		AND a.phone = #{phone}


		AND a.address = #{address}


		AND a.latitude = #{latitude}


		AND a.longitude = #{longitude}


		AND a.imgurl = #{imgurl}




		ORDER BY ${page.orderBy}

SELECT 

FROM afcustomers a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE afcustomers SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
