mapper.select:
SELECT a.aid, c. NAME AS customersName, d. NAME AS defencesName, a.AlarmType AS typeName, a.AlarmTime AS date, a.State, a.Remark FROM alarms a LEFT JOIN customers c ON a.CustomerID = c.CID LEFT JOIN defences d ON a.DefenceID = d.DID
1 = 1
and a.AlarmTime > #{startTime}
AND a.AlarmTime < #{endTime}
AND c.ParentCid =#{customerId}
ORDER BY a.AlarmTime DESC
