mapper.select:
SELECT

FROM areas a

WHERE a.id = #{id}

SELECT

FROM areas a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id}


		AND a.cid = #{cid}


		AND a.customertype = #{customertype}


		AND a.parent_ids LIKE
			'%'||#{parentIds}||'%'
			'%'+#{parentIds}+'%'
			concat('%',#{parentIds},'%')


		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.cid = #{cid}


ORDER BY a.create_date DESC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM areas a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE areas SET
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}
