mapper.update:
UPDATE customers SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE cid = #{cid}

mapper.select:
select a.name from (select id from areas where cid=#{cid}) d , areas a where a.parent_ids like CONCAT('%',d.id,'%')
union
SELECT name from areas where areas.cid =#{cid};

SELECT * from customers c

	1 = 1

		and c.DueTime  >  #{startTime}


		AND c.DueTime  <   #{endTime}

SELECT c.cid AS "cid", c.name AS "name", c.customertype AS "customertype", c.areaid AS "areaid", c.point AS "point", c.address AS "address", c.contacts AS "contacts", c.phone AS "phone", c.qualityperson AS "qualityperson", c.installperson AS "installperson", c.preparer AS "preparer", c.installtime AS "installtime", c.duetime AS "duetime", c.remark AS "remark", c.createtime AS "createtime", a.`name` AS areaName, s.name as createid FROM customers c LEFT JOIN areas a ON c.areaid = a.id LEFT JOIN sys_user s ON s.id = c.CreateBy WHERE c.ParentCid = #{cid} AND c.CreateBy = #{createid} AND c.CustomerType = #{customertype}
AND c.name LIKE concat('%',#{name},'%')
and c.DueTime >= #{startTime}
AND c.DueTime <= #{endTime}
ORDER BY c.createtime DESC

SELECT aa.*, bb.* FROM ( SELECT COUNT(mm.mid) AS masterNum, cc.cid AS id, cc.name, cc.customerType, cc.qualityPerson, cc.installPerson, cc.installTime, cc.createTime FROM customers cc LEFT JOIN masters mm ON cc.cid = mm.customerId WHERE 1=1
AND cc.CreateBy =#{id}
and cc.name like CONCAT('%',#{name},'%')
and cc.CustomerType = #{customertype}
GROUP BY cc.cid ) aa LEFT JOIN ( SELECT c.CID AS cid2, sum( CASE d.devicetype WHEN 1 THEN 1 ELSE 0 END ) AS DEVICETYPE1, sum( CASE d.devicetype WHEN 2 THEN 1 ELSE 0 END ) AS DEVICETYPE2, sum( CASE d.devicetype WHEN 3 THEN 1 ELSE 0 END ) AS DEVICETYPE3, sum( CASE d.devicetype WHEN 4 THEN 1 ELSE 0 END ) AS DEVICETYPE4, sum( CASE d.devicetype WHEN 5 THEN 1 ELSE 0 END ) AS DEVICETYPE5, sum( CASE d.devicetype WHEN 6 THEN 1 ELSE 0 END ) AS DEVICETYPE6, sum( CASE d.devicetype WHEN 7 THEN 1 ELSE 0 END ) AS DEVICETYPE7 FROM `devices` d LEFT JOIN masters m ON d.masterid = m.MID LEFT JOIN customers c ON m.CustomerID = c.CID GROUP BY c.CID ) bb ON aa.id = bb.cid2 ORDER BY aa.createTime DESC
